        {% emit_nulls = options && options[:emit_nulls] %}
        {% attributes = {} of Nil => Nil %}
        {% blocks = {} of Nil => Nil %}
        {% labels = {} of Nil => Nil %}
        {% current_label_idx = 0 %}
        {% for ivar in @type.instance_vars %}
          {% if ann = ivar.annotation(::HCL::Attribute) %}
            {% attributes[ivar.id] = {key: ((ann && ann[:key]) || ivar).id.stringify, emit_null: (ann && (ann[:emit_null] != nil) ? ann[:emit_null] : emit_nulls)} %}
          {% else %}{% if ann = ivar.annotation(::HCL::Block) %}
            {% blocks[ivar.id] = {key: ((ann && ann[:key]) || ivar).id.stringify} %}
          {% else %}{% if ann = ivar.annotation(::HCL::Label) %}
            {% labels[ivar.id] = {type: ivar.type, index: ann[:index] || current_label_idx} %}
            {% current_label_idx = ann[:index] ? (ann[:index] + 1) : (current_label_idx + 1) %}
          {% end %}{% end %}{% end %}
        {% end %}

        {% for name, value in attributes %}
          %var{name} = @{{ name }}

